import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Header.css';
import IconButton from '@material-ui/core/IconButton';
import SearchIcon from '@material-ui/icons/Search';
import { Button } from '@mui/material';
import { getUsers, deleteUser } from '../Service/api';

function Header() {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [activeTab, setActiveTab] = useState('Home');
  const location = useLocation();

  useEffect(() => {
    getAllUsers();
    if (location.pathname === '/') {
      setActiveTab('Home');
    } else if (location.pathname === '/add') {
      setActiveTab('AddUser');
    }
  }, [location]);

  const deleteUserData = async (id) => {
    await deleteUser(id);
    await getAllUsers();
  };

  return (
    <>
      <div className="header">
        <Link to="/">
          <p className="logo">User Management System</p>
        </Link>
        <div className="header-right">
          <Link to="/">
            {/* eslint-disable-next-line jsx-a11y/no-noninteractive-element-interactions */}
            <p
              className={`${activeTab === 'Home' ? 'active' : ''}`}
              onClick={() => setActiveTab('Home')}
            >
              All Items
            </p>
          </Link>

          <Link to="/add">
            {/* eslint-disable-next-line jsx-a11y/no-noninteractive-element-interactions */}
            <p
              className={`${activeTab === 'AddUser' ? 'active' : ''}`}
              onClick={() => setActiveTab('AddUser')}
            >
              Add Items
            </p>
          </Link>
        </div>
        <div className="search">
          <input
            type="text"
            placeholder="Search Item Name..."
            onChange={(event) => {
              setSearchTerm(event.target.value);
            }}
          />
          <IconButton className="icon">
            <SearchIcon />
          </IconButton>
        </div>
      </div>
      {/* eslint-disable-next-line react/void-dom-elements-no-children */}

      <div className="card-header">
        <p>Item Orders</p>
      </div>
      <div
        style={{
          display: 'grid',
          gridGap: '20px',
          gridTemplateColumns: 'repeat(auto-fill, minmax(350px, 1fr))'
        }}
      >
        {users
          .filter((user) => {
            if (searchTerm === '') {
              return user;
            }
            if (user.name.toLowerCase().includes(searchTerm.toLowerCase())) {
              return user;
            }
            return false;
          })
          .map((user) => (
            <div key={user._id} className="card">
              <div className="header">
                <strong>ID:</strong>
                <strong>{user._id}</strong>
              </div>
              <div className="container">
                <br />
                <br />
                <strong>Name: </strong>
                <strong>{user.name}</strong>
                <br />
                <br />
                <strong>Type: </strong>
                <strong>{user.type}</strong>
                <br />
                <br />
                <strong>Price: </strong>
                <strong> {user.price} ₮</strong>
                <br />
                <br />
                <strong className="button">
                  <button
                    type="button"
                    color=" secondary"
                    variant="contained"
                    onClick={() => deleteUserData(user._id)}
                  >
                    Delete
                  </button>
                  {/* eslint-disable-next-line react/button-has-type */}
                  <Button
                    className="btn"
                    variant="contained"
                    component={Link}
                    to={`/edit/${user._id}`}
                  >
                    Edit User
                  </Button>
                  {/* change it to user.id to use JSON Server */}
                </strong>
              </div>
            </div>
          ))}
      </div>
    </>
  );
}

export default Header;
