import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { addUser } from '../Service/api';

const initialValue = {
  name: '',
  type: '',
  price: ''
};

const User = styled.div`
  padding: 20px;
  .container {
  }

  .card-header {
    display: flex;
    justify-content: center;
    font-size: 20px;
    font-weight: 600;
  }

  label {
    margin-bottom: 10px;
    display: flex;
    justify-content: center;
  }
  input {
    width: 100%;
    margin-left: 20px;
  }

  .add {

  }
`;

function AddUser() {
  const [user, setUser] = useState(initialValue);
  const { name, type, price } = user;

  const navigate = useNavigate();

  const onValueChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const addUserDetails = async () => {
    await addUser(user);
    navigate('/all');
  };

  return (
    <User>
      <form
        style={{
          margin: 'auto',
          padding: '15px',
          maxWidth: '400px',
          alignContent: 'center',
          display: 'grid',
          border: '1px solid'
        }}
      >
        <label htmlFor="name">
          Name
          <input
            onChange={(e) => onValueChange(e)}
            type="text"
            id="name"
            name="name"
            placeholder="Enter Product Name ..."
            value={name}
          />
        </label>

        <label htmlFor="name">
          Type
          <input
            onChange={(e) => onValueChange(e)}
            type="type"
            id="type"
            name="type"
            placeholder="Enter Product Type ..."
            value={type}
          />
        </label>

        <label htmlFor="name">
          Price
          <input
            onChange={(e) => onValueChange(e)}
            type="number"
            id="price"
            name="price"
            placeholder="Enter Product Price ..."
            value={price}
          />
        </label>

        <input
          className="add"
          type="submit"
          value="Add"
          onClick={() => addUserDetails()}
        />
      </form>
    </User>
  );
}

export default AddUser;
