import React, { useEffect, useState } from 'react';
import './App.css';
import {
  BrowserRouter,
  Routes,
  Route,
  useSearchParams
} from 'react-router-dom';
import 'react-toastify/dist/ReactToastify.css';

import Header from './components/Header';
import AddUser from './pages/AddUser';
import EditUser from './pages/EditUser';
import AllItems from './pages/AllItems';
import { getUsers } from './Service/api';

function App() {
  const [users, setUsers] = useState([]);
  const [searchParams, setSearchParams] = useSearchParams();
  const getAllUsers = async (name) => {
    const response = await getUsers(`?sort=name:ASC&q=${name}`);
    setUsers(response.data);
  };

  useEffect(() => {
    getAllUsers(searchParams.get('q'));
  }, [searchParams]);

  return (
    <BrowserRouter>
      <div className="App">
        <Header searchParams={searchParams} users={users}/>
        <Routes>
          {' '}
          <Route
            path="/"
            element={<AllItems searchParams={searchParams} users={{ users }} />}
          />
          <Route path="/add" element={<AddUser />} />
          <Route path="/edit/:id" element={<EditUser />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
