import axios from 'axios';

// const usersUrl = 'http://localhost:3003/users';
const usersUrl = 'http://localhost:5000';

export const getUsers = async (id) => {
  id = id || '';
  return await axios.get(`${usersUrl}/${id}?sort=name:ASC`);
};

export const addUser = async (user) =>
  // eslint-disable-next-line no-return-await
  await axios.post(`${usersUrl}/add`, user);

export const deleteUser = async (id) => await axios.delete(`${usersUrl}/${id}`);

export const editUser = async (id, user) =>
  // eslint-disable-next-line no-return-await
  await axios.put(`${usersUrl}/${id}`, user);
