import React, { useState } from 'react';
import styled from 'styled-components';
import Accessibility from './accessibility';
import MenuToggle from './menuToggle';

const NavLinksContainer = styled.div`
  height: 100%;
  display: flex;
  align-items: center;
  padding-top: 20px;
  padding-bottom: 16px;
`;

const LinksWrapper = styled.ul`
  .dropdown {
    margin: 0;
    padding: 0;
    display: flex;
    height: 100%;
    list-style: none;
    background-color: #fff;
    width: 100%;
    flex-direction: column;
    position: absolute;
    top: 65px;
    left: 0;
    margin-top: 6px;
    z-index: 22;
    opacity: 0;
    transition: opacity 150ms ease-in-out;
    transform: translateY(-10px);
  }

  .dropdown-menu {
    margin: 0;
    padding: 0;
    display: flex;
    height: 100%;
    list-style: none;
    background-color: #fff;
    width: 100%;
    flex-direction: column;
    position: absolute;
    top: 65px;
    left: 0;
    margin-top: 6px;
    z-index: 22;
    opacity: 1;
    transform: translateY(0);
  }
`;

const LinkItem = styled.li`
  width: 100%;
  padding: 0 1.1em;
  color: #222;
  font-weight: 500;
  font-size: 16px;
  display: flex;
  margin-bottom: 10px;
`;

const Link = styled.a`
  text-decoration: none;
  color: inherit;
  font-size: inherit;
`;

const Marginer = styled.div`
  height: 0.6em;
`;
function MobileNavLinks() {
  const [isOpen, setOpen] = useState(false);
  const [menu, setMenu] = useState(false);
