import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import Accessibility from './accessibility';
import MenuToggle from './menuToggle';

const NavLinksContainer = styled.div`
  height: 100%;
  display: flex;
  align-items: center;
  padding-top: 20px;
  padding-bottom: 16px;
`;

const LinksWrapper = styled(motion.div)`
  //margin: 0;
  //padding: 0;
  //display: flex;
  //height: 100%;
  //list-style: none;
  //background-color: #fff;
  //width: 100%;
  //flex-direction: column;
  //position: fixed;
  //top: 0;
  //left: 0;
  //margin-top: 6px;
  //z-index: 22;
  //user-select: none;
  min-width: 300px;
  width: 100%;
  max-width: 44%;
  height: 100%;
  background-color: #fff;
  box-shadow: -2px 0 2px rgba(15, 15, 15, 0.3);
  z-index: 90;
  position: fixed;
  top: 0;
  right: 0;
  transform: translateX(4em);
  user-select: none;
  padding: 1em 2.5em;
`;

const LinkItem = styled.li`
  width: 100%;
  padding: 0 1.1em;
  color: #222;
  font-weight: 500;
  font-size: 16px;
  display: flex;
  margin-bottom: 10px;
`;

const Link = styled.a`
  text-decoration: none;
  color: inherit;
  font-size: inherit;
`;

const Marginer = styled.div`
  height: 0.6em;
`;

const menuVariants = {
  open: {
    transform: 'translateX(3%)'
  },
  closed: {
    transform: 'translateX(103%)'
  }
};

const menuTransition = {
  type: 'spring',
  duration: 1,
  stiffness: 33,
  delay: 0.1
};

// const menuVariants = {
//   open: {
//     transform: 'translateY(13%)'
//   },
//   closed: {
//     transform: 'translateY(143%)'
//   }
// };

// const menuTransition = {
//   type: 'spring',
//   duration: 21,
//   stiffness: 33,
//   delay: 0.1
// };

function MobileNavLinks() {
  const [isOpen, setOpen] = useState(false);

  if (isOpen) {
    document.body.style.overflow = 'hidden';
  } else {
    document.body.style.overflow = 'scroll';
  }

  return (
    <NavLinksContainer>
      <MenuToggle isOpen={isOpen} toggle={() => setOpen(!isOpen)} />
      {isOpen && (
        <LinksWrapper
          initial={false}
          animate={isOpen ? 'open' : 'closed'}
          variants={menuVariants}
          transition={menuTransition}
        >
          <LinkItem>
            <Link href="/">All Items</Link>
          </LinkItem>
          <LinkItem>
            <Link href="/add">Add Items</Link>
          </LinkItem>
          <Marginer />
          <Accessibility />
        </LinksWrapper>
      )}
    </NavLinksContainer>
  );
}

export default MobileNavLinks;
